mapper.select:
SELECT 
FROM service_record sr
LEFT JOIN elderly_profile ep ON sr.elderly_id = ep.id


        AND ep.name LIKE CONCAT('%', #{elderlyName}, '%')


        AND sr.service_content LIKE CONCAT('%', #{serviceContent}, '%')


        AND sr.service_provider_name LIKE CONCAT('%', #{serviceProviderName}, '%')


        AND sr.status = #{status}


        AND sr.service_time >= #{startTime}


        AND sr.service_time <= #{endTime}


ORDER BY sr.service_time DESC, sr.id DESC

SELECT count(sr.id)
FROM service_record sr
LEFT JOIN elderly_profile ep ON sr.elderly_id = ep.id


        AND ep.name LIKE CONCAT('%', #{elderlyName}, '%')


        AND sr.service_content LIKE CONCAT('%', #{serviceContent}, '%')


        AND sr.service_provider_name LIKE CONCAT('%', #{serviceProviderName}, '%')


        AND sr.status = #{status}


        AND sr.service_time >= #{startTime}


        AND sr.service_time <= #{endTime}

mapper.update:
UPDATE service_record
elderly_id = #{elderlyId}, service_content = #{serviceContent}, service_time = #{serviceTime}, service_address = #{serviceAddress}, service_provider_type = #{serviceProviderType}, service_provider_id = #{serviceProviderId}, service_provider_name = #{serviceProviderName}, work_order_price = #{workOrderPrice}, status = #{status}, evaluation_score = #{evaluationScore}, evaluation_comment = #{evaluationComment}, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.delete:
DELETE FROM service_record
WHERE id IN

    #{id}
